	void SetColor(Texture2D tex, Color col)
	{
		Color[] colors = new Color[Mathf.RoundToInt(tex.width * tex.height)];
		for(int i = 0; i < colors.Length; i++)
		{
			colors[i] = col;
		}
		tex.SetPixels(colors);
		tex.Apply();
	}
	Color HSVtoRGB(float h, float s, float v )
	{
		int i;
		float f, p, q, t;
		Color outColor = new Color();
		outColor.a = 1;
		if( s == 0 ) {
			// achromatic (grey)
			outColor.r = outColor.g = outColor.b = v;
			return outColor;
		}
		h /= 60;			// sector 0 to 5
		i = Mathf.FloorToInt(h);
		f = h - i;			// factorial part of h
		p = v * ( 1 - s );
		q = v * ( 1 - s * f );
		t = v * ( 1 - s * ( 1 - f ) );
		
		switch( i ) {
		case 0:
			outColor.r = v;
			outColor.g = t;
			outColor.b = p;
			break;
		case 1:
			outColor.r = q;
			outColor.g = v;
			outColor.b = p;
			break;
		case 2:
			outColor.r = p;
			outColor.g = v;
			outColor.b = t;
			break;
		case 3:
			outColor.r = p;
			outColor.g = q;
			outColor.b = v;
			break;
		case 4:
			outColor.r = t;
			outColor.g = p;
			outColor.b = v;
			break;
		default:		// case 5:
			outColor.r = v;
			outColor.g = p;
			outColor.b = q;
			break;
		}
		return outColor;
	}
